<script setup lang="ts">
interface Props {
  modifierClasses?: string;
  name?: string;
  label?: string;
  type?: string;
  check?: boolean;
}

withDefaults(defineProps<Props>(), {
  modifierClasses: "",
  name: "switch",
  label: "Switch Label",
  type: "checkbox",
  check: false,
});
const emit = defineEmits(["change"]);
</script>

<template>
  <div class="field">
    <input
      :id="name"
      type="checkbox"
      :name="name"
      class="switch is-rounded"
      :class="modifierClasses"
      :checked="check"
      @change="$emit('change')"
    />
    <label :for="name">{{ label }}</label>
  </div>
</template>
