<script setup lang="ts">
import { ref, computed, watch, nextTick } from "vue";

const accentColor = ref("#1971bd");
const fontColor = ref("#1c1c1c");

const convertToHsl = (H: string) => {
  // Convert hex to RGB first
  let r = 0,
    g = 0,
    b = 0;
  if (H.length == 4) {
    r = Number("0x" + H[1] + H[1]);
    g = Number("0x" + H[2] + H[2]);
    b = Number("0x" + H[3] + H[3]);
  } else if (H.length == 7) {
    r = Number("0x" + H[1] + H[2]);
    g = Number("0x" + H[3] + H[4]);
    b = Number("0x" + H[5] + H[6]);
  }
  // Then to HSL
  r /= 255;
  g /= 255;
  b /= 255;
  let cmin = Math.min(r, g, b),
    cmax = Math.max(r, g, b),
    delta = cmax - cmin,
    h = 0,
    s = 0,
    l = 0;

  if (delta == 0) h = 0;
  else if (cmax == r) h = ((g - b) / delta) % 6;
  else if (cmax == g) h = (b - r) / delta + 2;
  else h = (r - g) / delta + 4;

  h = Math.round(h * 60);

  if (h < 0) h += 360;

  l = (cmax + cmin) / 2;
  s = delta == 0 ? 0 : delta / (1 - Math.abs(2 * l - 1));
  s = +(s * 100).toFixed(1);
  l = +(l * 100).toFixed(1);

  return {
    hue: `${h}`,
    saturation: `${s}%`,
    lightness: `${l}%`,
  };
};

const accentHsl = computed(() => convertToHsl(accentColor.value));
const fontHsl = computed(() => convertToHsl(fontColor.value));

watch(
  accentHsl,
  (n) => {
    const { hue, saturation, lightness } = n || {};
    nextTick(() => {
      document.documentElement.style.setProperty("--hsl-hue", hue);
      document.documentElement.style.setProperty("--hsl-saturation", saturation);
      document.documentElement.style.setProperty("--hsl-lightness", lightness);
    });
  },
  { deep: true }
);

watch(
  fontHsl,
  (n) => {
    const { hue, saturation, lightness } = n || {};
    nextTick(() => {
      document.documentElement.style.setProperty("--font-hsl-hue", hue);
      document.documentElement.style.setProperty("--font-hsl-saturation", saturation);
      document.documentElement.style.setProperty("--font-hsl-lightness", lightness);
    });
  },
  { deep: true }
);
</script>

<template>
  <div class="is-flex is-align-items-center is-justify-content-flex-end mr-16">
    <input title="Accent Color" type="color" v-model="accentColor" />
    <input title="Font Color" type="color" v-model="fontColor" />
  </div>
</template>
