<script setup lang="ts">
import { defineAsyncComponent } from "vue";

const TabsBody = defineAsyncComponent(() => import("../tab/TabsBody.vue"));
const Tabs = defineAsyncComponent(() => import("../tab/Tabs.vue"));
const TabItem = defineAsyncComponent(() => import("../tab/TabItem.vue"));

interface Props {
  activeTab?: string;
}

withDefaults(defineProps<Props>(), {
  activeTab: "",
});

defineEmits(["tabchange"]);
</script>

<template>
  <tabs-body>
    <tabs class="is-line">
      <tab-item :is-active="activeTab === 'edit'">
        <a @click.prevent="$emit('tabchange', 'edit')">Edit</a>
      </tab-item>
      <tab-item :is-active="activeTab === 'preview'">
        <a @click.prevent="$emit('tabchange', 'preview')">Preview Changes</a>
      </tab-item>
    </tabs>
  </tabs-body>
</template>
